mapper.select:
	SELECT QA.QA_NO, QA.Q_NO, QA.ANSWER_YN, QA.SECRET_YN, QA.TITLE, QA.CONTENTS, QA.REG_USER_NO, DATE_FORMAT(QA.REG_DT, '%Y-%M-%D') AS REG_DT
    FROM QA_INFO_A QA
    WHERE QA.QA_NO = #{qa_no}

	SELECT QA.QA_NO, QA.Q_NO, QA.ANSWER_YN, QA.SECRET_YN, QA.TITLE, CONTENTS, QA.REG_USER_NO, DATE_FORMAT(QA.REG_DT, '%Y-%M-%D') AS REG_DT
    FROM QA_INFO_A QA
    WHERE QA.QA_NO = #{qa_no}
